mapper.select:
select s.s_no, p.p_userid 
from s_enroll s, s_parti p
where  s.s_no = p.s_no
and s.s_no = #{s_no}
and p.p_userid = #{p_userid}

mapper.delete:
delete from s_parti
where s_no = #{s_no}
and p_userid = #{p_userid}

mapper.update:
update s_parti
set starscore_parti = 1
where s_no = #{s_no}
and p_userid = #{p_userid}

mapper.select:
select *from s_parti
where s_no = #{s_no}
and p_userid = #{p_userid}

select t.t_no, p.p_userid 
from t_enroll t, t_parti p
where  t.t_no = p.t_no
and t.t_no = #{t_no}
and p.p_userid = #{p_userid}

mapper.delete:
delete from t_parti
where t_no = #{t_no}
and p_userid = #{p_userid}

mapper.update:
update t_parti
set starscore_parti = 1
where t_no = #{t_no}
and p_userid = #{p_userid}
